export default {
  name: '',
  components: {},
  data: function data() {
    return {
      items: [{
        type: '',
        label: '标签一'
      }, {
        type: 'success',
        label: '标签二'
      }, {
        type: 'info',
        label: '标签三'
      }, {
        type: 'danger',
        label: '标签四'
      }, {
        type: 'warning',
        label: '标签五'
      }],
      dynamicTags: ['标签一', '标签二', '标签三'],
      inputVisible: false,
      inputValue: '',
      tags: [{
        name: '标签一',
        type: ''
      }, {
        name: '标签二',
        type: 'success'
      }, {
        name: '标签三',
        type: 'info'
      }, {
        name: '标签四',
        type: 'warning'
      }, {
        name: '标签五',
        type: 'danger'
      }],
      text: '',

      /* eslint-disable */
      base: "     \n        <el-tag>\u6807\u7B7E\u4E00</el-tag>\n        <el-tag type=\"success\">\u6807\u7B7E\u4E8C</el-tag>\n        <el-tag type=\"info\">\u6807\u7B7E\u4E09</el-tag>\n        <el-tag type=\"warning\">\u6807\u7B7E\u56DB</el-tag>\n        <el-tag type=\"danger\">\u6807\u7B7E\u4E94</el-tag>",
      closable: "\n       <el-tag\n        v-for=\"tag in tags\"\n        :key=\"tag.name\"\n        closable\n        @close=\"handleCloseTag(tag)\"\n        :type=\"tag.type\">\n        {{tag.name}}\n      </el-tag>\n\n      <script>\n        export default {\n          data() {\n            return {\n              tags: [\n                { name: '\u6807\u7B7E\u4E00', type: '' },\n                { name: '\u6807\u7B7E\u4E8C', type: 'success' },\n                { name: '\u6807\u7B7E\u4E09', type: 'info' },\n                { name: '\u6807\u7B7E\u56DB', type: 'warning' },\n                { name: '\u6807\u7B7E\u4E94', type: 'danger' }\n              ]\n            };\n          methods: {\n            handleCloseTag(tag) {\n              this.tags.splice(this.tags.indexOf(tag), 1);\n            },\n          }\n        }\n      </script>",
      dynamic: "\n           <el-tag\n            :key=\"tag\"\n            v-for=\"tag in dynamicTags\"\n            closable\n            :disable-transitions=\"false\"\n            @close=\"handleClose(tag)\">\n            {{tag}}\n          </el-tag>\n          <el-input\n            class=\"input-new-tag\"\n            v-if=\"inputVisible\"\n            v-model=\"inputValue\"\n            ref=\"saveTagInput\"\n            size=\"small\"\n            @keyup.enter.native=\"handleInputConfirm\"\n            @blur=\"handleInputConfirm\"\n          >\n          </el-input>\n          <el-button v-else class=\"button-new-tag\" size=\"small\" @click=\"showInput\">+ New Tag</el-button>\n\n          <style>\n            .el-tag + .el-tag {\n              margin-left: 10px;\n            }\n            .button-new-tag {\n              margin-left: 10px;\n              height: 32px;\n              line-height: 30px;\n              padding-top: 0;\n              padding-bottom: 0;\n            }\n            .input-new-tag {\n              width: 90px;\n              margin-left: 10px;\n              vertical-align: bottom;\n            }\n            .input-new-tag /deep/ .el-input__inner {\n              height: 24px;\n            }\n          </style>\n\n          <script>\n            export default {\n              data() {\n                return {\n                  dynamicTags: ['\u6807\u7B7E\u4E00', '\u6807\u7B7E\u4E8C', '\u6807\u7B7E\u4E09'],\n                  inputVisible: false,\n                  inputValue: ''\n                };\n              },\n              methods: {\n                handleClose(tag) {\n                  this.dynamicTags.splice(this.dynamicTags.indexOf(tag), 1);\n                },\n\n                showInput() {\n                  this.inputVisible = true;\n                  this.$nextTick(_ => {\n                    this.$refs.saveTagInput.$refs.input.focus();\n                  });\n                },\n\n                handleInputConfirm() {\n                  let inputValue = this.inputValue;\n                  if (inputValue) {\n                    this.dynamicTags.push(inputValue);\n                  }\n                  this.inputVisible = false;\n                  this.inputValue = '';\n                }\n              }\n            }\n          </script>",
      size: "\n            <el-tag closable>\u9ED8\u8BA4\u6807\u7B7E</el-tag>\n            <el-tag size=\"medium\" closable>\u4E2D\u7B49\u6807\u7B7E</el-tag>\n            <el-tag size=\"small\" closable>\u5C0F\u578B\u6807\u7B7E</el-tag>\n            <el-tag size=\"mini\" closable>\u8D85\u5C0F\u6807\u7B7E</el-tag>\n            ",
      theme: "<div class=\"tag-group\">\n              <span class=\"tag-group__title\">Dark</span>\n              <el-tag\n                v-for=\"item in items\"\n                :key=\"item.label\"\n                :type=\"item.type\"\n                effect=\"dark\">\n                {{ item.label }}\n              </el-tag>\n            </div>\n            <div class=\"tag-group\">\n              <span class=\"tag-group__title\">Plain</span>\n              <el-tag\n                v-for=\"item in items\"\n                :key=\"item.label\"\n                :type=\"item.type\"\n                effect=\"plain\">\n                {{ item.label }}\n              </el-tag>\n            </div>\n\n            <script>\n              export default {\n                data() {\n                  return {\n                    items: [\n                      { type: '', label: '\u6807\u7B7E\u4E00' },\n                      { type: 'success', label: '\u6807\u7B7E\u4E8C' },\n                      { type: 'info', label: '\u6807\u7B7E\u4E09' },\n                      { type: 'danger', label: '\u6807\u7B7E\u56DB' },\n                      { type: 'warning', label: '\u6807\u7B7E\u4E94' }\n                    ]\n                  }\n                }\n              }\n            </script>"
    };
  },
  // 计算属性设置
  computed: {},
  // 数据变更监听
  watch: {},
  mounted: function mounted() {
    this.init();
  },
  methods: {
    handleInputConfirm: function handleInputConfirm() {
      var inputValue = this.inputValue;

      if (inputValue) {
        this.dynamicTags.push(inputValue);
      }

      this.inputVisible = false;
      this.inputValue = '';
    },
    handleCloseTag: function handleCloseTag(tag) {
      this.tags.splice(this.tags.indexOf(tag), 1);
    },
    handleClose: function handleClose(tag) {
      this.dynamicTags.splice(this.dynamicTags.indexOf(tag), 1);
    },
    showInput: function showInput() {
      var _this = this;

      this.inputVisible = true;
      this.$nextTick(function () {
        _this.$refs.saveTagInput.$refs.input.focus();
      });
    },
    // 执行获取数据等初始化动作
    init: function init() {}
  }
};
