export default {
  name: 'LinCmsUiButton',
  components: {},
  data: function data() {
    return {
      /* eslint-disable */
      base: "     \n        <template>\n          <el-button :plain=\"true\" @click=\"open\">\u6253\u5F00\u6D88\u606F\u63D0\u793A</el-button>\n          <el-button :plain=\"true\" @click=\"openVn\">VNode</el-button>\n        </template>\n\n        <script>\n          export default {\n            methods: {\n              open() {\n                this.$message('\u8FD9\u662F\u4E00\u6761\u6D88\u606F\u63D0\u793A')\n              },\n\n              openVn() {\n                const h = this.$createElement\n                this.$message({\n                  message: h('p', null, [\n                    h('span', null, '\u5185\u5BB9\u53EF\u4EE5\u662F '),\n                    h('i', { style: 'color: teal' }, 'VNode')\n                  ])\n                })\n              }\n            }\n          }\n        </script>",
      type: "\n      <template>\n        <el-button :plain=\"true\" @click=\"open2\">\u6210\u529F</el-button>\n        <el-button :plain=\"true\" @click=\"open3\">\u8B66\u544A</el-button>\n        <el-button :plain=\"true\" @click=\"open1\">\u6D88\u606F</el-button>\n        <el-button :plain=\"true\" @click=\"open4\">\u9519\u8BEF</el-button>\n      </template>\n\n      <script>\n        export default {\n          methods: {\n            open1() {\n              this.$message('\u8FD9\u662F\u4E00\u6761\u6D88\u606F\u63D0\u793A')\n            },\n            open2() {\n              this.$message({\n                message: '\u606D\u559C\u4F60\uFF0C\u8FD9\u662F\u4E00\u6761\u6210\u529F\u6D88\u606F',\n                type: 'success'\n              })\n            },\n\n            open3() {\n              this.$message({\n                message: '\u8B66\u544A\u54E6\uFF0C\u8FD9\u662F\u4E00\u6761\u8B66\u544A\u6D88\u606F',\n                type: 'warning'\n              });\n            },\n\n            open4() {\n              this.$message.error('\u9519\u4E86\u54E6\uFF0C\u8FD9\u662F\u4E00\u6761\u9519\u8BEF\u6D88\u606F');\n            }\n          }\n        }\n      </script>",
      showClose: "\n        <template>\n          <el-button :plain=\"true\" @click=\"open1\">\u6D88\u606F</el-button>\n          <el-button :plain=\"true\" @click=\"open2\">\u6210\u529F</el-button>\n          <el-button :plain=\"true\" @click=\"open3\">\u8B66\u544A</el-button>\n          <el-button :plain=\"true\" @click=\"open4\">\u9519\u8BEF</el-button>\n        </template>\n\n        <script>\n          export default {\n            methods: {\n              open1() {\n                this.$message({\n                  showClose: true,\n                  message: '\u8FD9\u662F\u4E00\u6761\u6D88\u606F\u63D0\u793A'\n                });\n              },\n\n              open2() {\n                this.$message({\n                  showClose: true,\n                  message: '\u606D\u559C\u4F60\uFF0C\u8FD9\u662F\u4E00\u6761\u6210\u529F\u6D88\u606F',\n                  type: 'success'\n                });\n              },\n\n              open3() {\n                this.$message({\n                  showClose: true,\n                  message: '\u8B66\u544A\u54E6\uFF0C\u8FD9\u662F\u4E00\u6761\u8B66\u544A\u6D88\u606F',\n                  type: 'warning'\n                });\n              },\n\n              open4() {\n                this.$message({\n                  showClose: true,\n                  message: '\u9519\u4E86\u54E6\uFF0C\u8FD9\u662F\u4E00\u6761\u9519\u8BEF\u6D88\u606F',\n                  type: 'error'\n                });\n              }\n            }\n          }\n        </script>",
      center: "\n        <template>\n          <el-button :plain=\"true\" @click=\"openCenter\">\u6587\u5B57\u5C45\u4E2D</el-button>\n        </template>\n\n        <script>\n          export default {\n            methods: {\n              openCenter() {\n                this.$message({\n                  message: '\u5C45\u4E2D\u7684\u6587\u5B57',\n                  center: true\n                });\n              }\n            }\n          }\n        </script>\n       ",
      dangerouslyUseHTMLString: "\n        <template>\n          <el-button :plain=\"true\" @click=\"openHTML\">\u4F7F\u7528 HTML \u7247\u6BB5</el-button>\n        </template>\n\n        <script>\n          export default {\n            methods: {\n              openHTML() {\n                this.$message({\n                  dangerouslyUseHTMLString: true,\n                  message: '<strong>\u8FD9\u662F <i>HTML</i> \u7247\u6BB5</strong>'\n                });\n              }\n            }\n          }\n        </script>"
    };
  },
  // 计算属性设置
  computed: {},
  // 数据变更监听
  watch: {},
  mounted: function mounted() {
    this.init();
