  methods: {
    openHTML: function openHTML() {
      this.$message({
        dangerouslyUseHTMLString: true,
        message: '<strong>这是 <i>HTML</i> 片段</strong>'
      });
    },
    openCenter: function openCenter() {
      this.$message({
        message: '居中的文字',
        center: true
      });
    },
    open: function open() {
      this.$message('这是一条消息提示');
    },
    openVn: function openVn() {
      var h = this.$createElement;
      this.$message({
        message: h('p', null, [h('span', {
          style: 'color: #3963BC'
        }, '内容可以是 '), h('i', {
          style: 'color: #3963BC'
        }, 'VNode')])
      });
    },
    open1: function open1() {
      this.$message('这是一条消息提示');
    },
    open2: function open2() {
      this.$message({
        message: '恭喜你，这是一条成功消息',
        type: 'success'
      });
    },
    open3: function open3() {
      this.$message({
        message: '警告哦，这是一条警告消息',
        type: 'warning'
      });
    },
    open4: function open4() {
      this.$message.error('错了哦，这是一条错误消息');
    },
    open5: function open5() {
      this.$message({
        showClose: true,
        message: '这是一条消息提示'
      });
    },
    open6: function open6() {
      this.$message({
        showClose: true,
        message: '恭喜你，这是一条成功消息',
        type: 'success'
      });
    },
    open7: function open7() {
      this.$message({
        showClose: true,
        message: '警告哦，这是一条警告消息',
        type: 'warning'
      });
    },
    open8: function open8() {
      this.$message({
        showClose: true,
        message: '错了哦，这是一条错误消息',
        type: 'error'
      });
    },
    // 执行获取数据等初始化动作
